package operators;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;

		num += 5;
		System.out.println("After += : " + num);

		num -= 3;
		System.out.println("After -= : " + num);

		num *= 2;
		System.out.println("After *= : " + num);

		num /= 4;
		System.out.println("After /= : " + num);

		num %= 3;
		System.out.println("After %= : " + num);


	}

}
